sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (oldValue != newValue) {
        var int1 = g_form.getValue('u_int');
        var sum = int1 * g_form.getValue('u_shuliang');
        alert('int1=' + int1 +
            ',\n sum=' + sum);
        g_form.setValue('u_sum', sum);
        alert("g_user.firstName = " + g_user.firstName +
            ", \n g_user.lastName = " + g_user.lastName +
            ", \n g_user.userName = " + g_user.userName +
            ", \n g_user.userID = " + g_user.userID +
            ", \n g_user.getFullName = " + g_user.getFullName() +
            ", \n g_user.hasRoleExactly = " + g_user.hasRoleExactly("admin"));

    }
    var getEmailAddr = new GlideAjax('ajaxGetEmail');
    getEmailAddr.addParam('sysparm_name', 'getEmail');
    // Pass the Requested for sys_id
    getEmailAddr.addParam('sysparm_userID', g_user.getFullName());
    // Send the request to the server
    getEmailAddr.getXML(populateEmailField);


    // When the response is back from the server
    function populateEmailField(response) {
        // Extract the email address from the response, clear any value from the email field, 
        // set new value in the email field
        var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		alert("emailFromScriptInclude = " +emailFromScriptInclude);
		 g_form.clearValue('email');
        g_form.setValue('email', emailFromScriptInclude);
    }
}
