sys_script_include.script:
var ajaxGetEmail = Class.create();
ajaxGetEmail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getEmail: function() {
        var userRecord = new GlideRecord("sys_user");
        //userRecord.get(this.getParameter('sysparm_userID'));
        userRecord.get(this.getParameter('sysparm_userID'));
		gs.info('this.getParameter='+this.getParameter('sysparm_userID'));
		gs.info('userRecord.email='+userRecord.email);
        return userRecord.email + '';

    },
    type: 'ajaxGetEmail'
});
